import styled from 'styled-components';

const Wrapper = styled.section`
  margin: 0 auto;

  .box_card {
    max-width: 117rem;
    margin: 0 auto;
    overflow: visible;
  }
  .flex_card {
    display: flex;
    gap: 3rem;
  }
  .flex_card .item {
    color: red;
    margin-right: 2px;
    width: 30rem;
    height: 11rem;
    background: yellow;
    cursor: pointer;
  }

  .flex_card::-webkit-scrollbar {
    width: 0;
  }
`;

export default Wrapper;
