import styled from 'styled-components';

const Wrapper = styled.nav`
  max-width: var(--max-width);
  margin: 0 auto;
  .top-header-center {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.8rem;
    max-width: 550px;
    margin: 0 auto;
    p {
      color: #fff;
      font-size: 14px;
      font-weight: 400;
    }
    a {
      color: #fff;
      font-weight: 600;
      font-size: 14px;
    }
  }

  .top-header {
    height: 4.8rem;
    background: #000;
    display: flex;
    align-items: center;
    justify-content: center;

    .top-header-right {
      width: var(--max-width-content);
      display: flex;
      align-items: center;
      justify-content: center;
    }

    .box-lang {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      p {
        color: #fff;
        font-size: 14px;
      }
      .select-lang {
        fill: #fff;
        font-size: 2rem;
      }
    }
  }

  .header {
    height: 4rem;
    background: red;
  }

  .content-header{
    width: var(--max-width-content);
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto;

    ul{
      width: 550px;
      margin: 0 auto;
    }
  }
`;

export default Wrapper;
