import styled from "styled-components";

const Wrapper = styled.aside`
display: flex;
  max-width: 117rem;
  margin: 0 auto;

  .content {
    ul {
      width: 23.3rem;
      padding-right: 1.6rem;
      box-shadow: 0.1rem 0 0 0 rgba(0,0,0,0.1);
      padding-top: 4rem;
      li {
        display: flex;
        align-items: center;
        justify-content: space-between;
        span {
          a {
            font-size: 1.6rem;
            font-weight: 400;
            color: var(--text-primary);
          }
        }
        margin-bottom: 1.6rem;
      }
      li:last-child {
        margin-bottom: 0;
      }
    }
  }

  .poster{
    width: 100%;
    height: 34.4rem;
    background: #000;
    margin: 4rem 0 0 4.5rem;
    display: flex;
    align-items: center;
    justify-content: space-between;

    .ip{
        display: flex;
        align-items: center;
        gap: 2.4rem;
    }

    p{
        color: #fff;
    }
  }
`;

export default Wrapper;
