import styled from "styled-components";

const Wrapper = styled.aside`
  max-width: 117rem;
  margin: 0 auto;
  padding: 4rem 0 0 0;

  .content {
    ul {
      width: 21.7rem;
      li {
        
        span {
          a {
            font-size: 1.6rem;
            font-weight: 400;
          }
        }
        margin-bottom: 1.6rem;
      }
      li:last-child {
        margin-bottom: 0;
      }
    }
  }
`;

export default Wrapper;
