import styled from 'styled-components';

const Wrapper = styled.section`
  .box_card {
    margin: 0 auto;
    max-width: 117rem;

    .btn_view {
      margin-bottom: 10rem;
      margin-top: 43px;
      text-align: center;
      padding: ;
    }
  }
  .flex_card {
    display: flex;
    gap: 3rem;
    overflow-x: auto;
    height: 37rem;
  }
  .flex_card .item {
    color: red;
    margin-right: 2px;
    width: 30rem;
    height: 11rem;
    background: yellow;
    cursor: pointer;
  }

  .flex_card::-webkit-scrollbar {
    width: 0;
  }
`;

export default Wrapper;
